print(*(lambda strings: (sum(map(lambda string: sum(letter in "aeiou" for letter in string) >= 3 and any(string[ind] == string[ind+1] for ind in range(len(string)-1)) and all(forbidden not in string for forbidden in ("ab", "cd", "pq", "xy")), strings)), sum(map(lambda string: any(string[ind] == string[ind+2] for ind in range(len(string)-2)) and any(string[ind:ind+2] in string[ind+2:] for ind in range(len(string)-1)), strings))))(open("Day5.txt").read().split("\n")), sep = "\n")
