print(*(lambda depths: (sum(1 if depths[index] > depths[index-1] else 0 for index in range(1, len(depths))), sum(1 if depths[index] + depths[index-1] + depths[index-2] > depths[index-1] + depths[index-2] + depths[index-3] else 0 for index in range(3, len(depths)))))(list(map(int, open("Day01.txt").read().splitlines()))), sep="\n")